$(document).ready(function (){
	$('body').tooltip({ selector: 'a[data-toggle=tooltip]' });


	// Add first-item and last-item classes for first and last elements
	$('.search-album-row').last().addClass('last-item');
	$('.jg-mod-row:last-child, .jg-mod-img:last-child, #component p:last-child').addClass('last-item');
	
	
	// Footer FB Feed
	$('#rss').rssfeed('https://www.facebook.com/feeds/page.php?format=rss20&id=309798195777900',{ 
		limit 	: 5,
		header	: 0,
		titletag: 'span',
		content	: 0,
		date	: 0
	});


	// Nav Tabs
	$('.nav-tabs a').click(function(e) {
	    e.preventDefault()
	    $(this).tab('show');
	});
	
		
	if ($(window).width() >= 768 && $(window).width() <= 1024){
		$(window).scroll(function() {
		    if ($(this).scrollTop()>40) {
		        $('#header  .site-title').fadeOut();
		        $('#topline .site-title').fadeIn();
			}
		    else {
				$('#header  .site-title').fadeIn();
		        $('#topline .site-title').fadeOut();
			}
		});
	}
	
	
	$('.btn-navbar').click( function(){
		if($(this).hasClass('open')){
			$(this).addClass('close').removeClass('open');
			$('#wrapper, #topline').addClass('push-from-center');
			$('#navmenu').addClass('push-to-center');
		} else {
			$(this).addClass('open').removeClass('close');
			$('#wrapper, #topline').removeClass('push-from-center');
			$('#navmenu').removeClass('push-to-center');
		}
	});
});

