function showabc(catid, itemid, pageid) {
    $('#component #ajaxload').html('<div style="padding:10px 50px; background:url(/templates/project/assets/img/loading_bg.gif) 0 0 no-repeat"><h5 style="margin:0;color:#a7a698">Идет Загрузка</h5></div>');
    
    jQuery.ajax({ 
        type: "GET",
        url: "/index.php?tmpl=ajax",
        data: "option=com_content&view=category&id="+ catid +"&start="+ pageid +"&Itemid="+ itemid,
        success: function(html){
            jQuery('#component #ajaxload').html(html);
        }
    });
}
